sect1.para:
function encodeBase64() {
  const input = document.getElementById("inputBox").innerText.trim();
  try {
    const encoded = btoa(unescape(encodeURIComponent(input)));
    document.getElementById("output").innerText = "Encoded Base64:\n" + encoded;
  } catch (e) {
    document.getElementById("output").innerText = "Error encoding text.";
  }
}

function decodeBase64() {
  const input = document.getElementById("inputBox").innerText.trim();
  try {
    const decoded = decodeURIComponent(escape(atob(input)));
    document.getElementById("output").innerText = "Decoded Text:\n" + decoded;
  } catch (e) {
    document.getElementById("output").innerText = "Invalid Base64 string.";
  }
}
