function calculateEMI() {
  const loanAmount = parseFloat(document.getElementById("loanAmount").value);
  const annualInterestRate = parseFloat(document.getElementById("interestRate").value);
  const tenure = parseInt(document.getElementById("loanTenure").value);

  if (!loanAmount || !annualInterestRate || !tenure || loanAmount <= 0 || annualInterestRate <= 0 || tenure <= 0) {
    document.getElementById("result").textContent = "Please enter valid inputs.";
    return;
  }

  const monthlyInterestRate = annualInterestRate / 12 / 100;
  const emi = (loanAmount * monthlyInterestRate * Math.pow(1 + monthlyInterestRate, tenure)) /
              (Math.pow(1 + monthlyInterestRate, tenure) - 1);

  const totalPayment = emi * tenure;
  const totalInterest = totalPayment - loanAmount;

  document.getElementById("result").innerHTML = `
    Monthly EMI: ₹${emi.toFixed(2)}<br/>
    Total Interest: ₹${totalInterest.toFixed(2)}<br/>
    Total Payment: ₹${totalPayment.toFixed(2)}
  `;
}
