function generatePassword() {
  const length = parseInt(document.getElementById("length").value);
  const count = parseInt(document.getElementById("count").value);
  const useUpper = document.getElementById("includeUpper").checked;
  const useLower = document.getElementById("includeLower").checked;
  const useNumbers = document.getElementById("includeNumbers").checked;
  const useSymbols = document.getElementById("includeSymbols").checked;
  const showPassword = document.getElementById("showPassword").checked;
  const resultBox = document.getElementById("passwordResult");

  const upper = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
  const lower = "abcdefghijklmnopqrstuvwxyz";
  const numbers = "0123456789";
  const symbols = "!@#$%^&*()_+[]{}|;:,.<>?";

  let allChars = "";
  if (useUpper) allChars += upper;
  if (useLower) allChars += lower;
  if (useNumbers) allChars += numbers;
  if (useSymbols) allChars += symbols;

  if (!allChars) {
    resultBox.textContent = "‚ö†Ô∏è Please select at least one option!";
    return;
  }

  let output = [];
  for (let j = 0; j < count; j++) {
    let password = "";
    for (let i = 0; i < length; i++) {
      password += allChars.charAt(Math.floor(Math.random() * allChars.length));
    }
    output.push(password);
  }

  resultBox.textContent = showPassword ? output.join("\n") : output.map(() => "‚óè‚óè‚óè‚óè‚óè‚óè‚óè‚óè‚óè‚óè‚óè‚óè").join("\n");
  resultBox.dataset.passwords = output.join("\n");
  evaluateStrength(output[0]);
}

function evaluateStrength(password) {
  const strengthBar = document.getElementById("strengthBar");
  const label = document.getElementById("strengthLabel");
  let score = 0;

  if (/[A-Z]/.test(password)) score++;
  if (/[a-z]/.test(password)) score++;
  if (/[0-9]/.test(password)) score++;
  if (/[^A-Za-z0-9]/.test(password)) score++;
  if (password.length >= 12) score++;

  let strength = ["Very Weak", "Weak", "Medium", "Strong", "Very Strong"];
  let colors = ["#ff4d4d", "#ff884d", "#ffd24d", "#7dff4d", "#4dff88"];
  strengthBar.style.width = (score * 20) + "%";
  strengthBar.style.background = colors[score - 1] || "#ccc";
  label.textContent = `Strength: ${strength[score - 1] || "N/A"}`;
}

function copyPassword() {
  const text = document.getElementById("passwordResult").dataset.passwords;
  if (!text) return alert("Generate a password first!");
  navigator.clipboard.writeText(text);
  alert("‚úÖ Password copied to clipboard!");
}

function downloadPasswords() {
  const text = document.getElementById("passwordResult").dataset.passwords;
  if (!text) return alert("Generate a password first!");
  const blob = new Blob([text], { type: "text/plain" });
  const a = document.createElement("a");
  a.href = URL.createObjectURL(blob);
  a.download = "passwords.txt";
  a.click();
}

function toggleMode() {
  document.body.classList.toggle("dark");
  const btn = document.querySelector(".toggle-mode");
  btn.textContent = document.body.classList.contains("dark") ? "‚òÄÔ∏è Light" : "üåô Dark";
}
